export function lazyLoadSelectedImages(images) {
    return new Promise((resolve) => {
        if (!images) {
            resolve();
            return;
        }
        images.forEach((image) => {
            if (image.hasAttribute('data-src')) {
                image.setAttribute('src', `${image.getAttribute('data-src')}`);
                image.removeAttribute('data-src');
                if (!image.classList.contains('deckgo-reveal')) {
                    image.style.setProperty('visibility', 'inherit');
                }
            }
            image.style.setProperty('pointer-events', 'none');
        });
        resolve();
    });
}
export function lazyLoadSelectedLazyImagesComponent(components) {
    return new Promise((resolve) => {
        if (!components) {
            resolve();
            return;
        }
        components.forEach(async (component) => {
            await component.lazyLoad();
        });
        resolve();
    });
}
